  const readmeContent = `# Claude Code Documentation

## Documentation Files

${successful.map(result => 
  `- **[${result.title}](${result.filename})** - ${getDescription(result.name)}`
).join('\n')}

## Additional Resources

- [Overview](https://docs.anthropic.com/en/docs/claude-code/overview)
- [Quickstart](https://docs.anthropic.com/en/docs/claude-code/quickstart)
- [Common Workflows](https://docs.anthropic.com/en/docs/claude-code/common-workflows)

## Updates

Run \`~/Developer/Knowledge/Scripts/update-claude-docs.sh\` to refresh.`;

  await fs.writeFile(path.join(DOCS_DIR, 'README.md'), readmeContent);
}

function getDescription(name) {
  const descriptions = {
    'getting-started': 'Installation and authentication',
    'memory': 'Memory management system',
    'mcp': 'Model Context Protocol integration',
    'hooks': 'Event-driven automation',
    'subagents': 'Specialized task delegation',
    'slash-commands': 'Built-in command reference',
    'settings': 'Configuration options'
  };
  return descriptions[name] || 'Documentation';
}

// Run the optimized extraction
if (require.main === module) {
  extractDocumentation()
    .then(() => {
      console.log('\nğŸ‰ Optimized documentation extraction completed!');
      process.exit(0);
    })
    .catch((error) => {
      console.error('\nğŸ’¥ Extraction failed:', error);
      process.exit(1);
    });
}

module.exports = { extractDocumentation };

