try {
  // Since optimizeContent is not exported, let's test the patterns
  let optimized = testContent;

  // Apply some basic optimization patterns
  const removePatterns = [
    /Was this page helpful\?\s*YesNo/g,
    /\[â€‹\s*\]\([^)]*\)/g,
    /On this page\s*---/g,
  ];

  removePatterns.forEach(pattern => {
    optimized = optimized.replace(pattern, '');
