  );

  optimized = optimized.replace(/\n{3,}/g, '\n\n').trim();

  console.log('Optimized length:', optimized.length);
  console.log('Reduction:', Math.round((1 - optimized.length / testContent.length) * 100) + '%');
  console.log('\nOptimized result:');
  console.log('---');
  console.log(optimized);
  console.log('---');

  console.log('\nâœ… Optimization patterns working correctly!');

} catch (error) {
  console.error('âŒ Test failed:', error.message);
}

