#!/bin/bash
# .claude/hooks/setup-hooks.sh
# Initialize the hook system

HOOK_DIR="$(dirname "$0")"

echo "üîß Setting up Hook Helper Library"

# Create directory structure
mkdir -p "$HOOK_DIR/lib" "$HOOK_DIR/logs" "$HOOK_DIR/.cache"

# Make scripts executable
chmod +x "$HOOK_DIR"/*.sh "$HOOK_DIR/lib"/*.sh 2>/dev/null

# Create sample settings.json if it doesn't exist
SETTINGS_FILE="$(dirname "$HOOK_DIR")/settings.json"
if [[ ! -f "$SETTINGS_FILE" ]]; then
    cat > "$SETTINGS_FILE" <<'JSON'
{
  "hooks": {
    "PreToolUse": [
      {
        "matcher": "Write|Edit",
        "hooks": [
          {
            "type": "command",
            "command": "$CLAUDE_PROJECT_DIR/.claude/hooks/validate-code.sh"
          }
        ]
      }
    ],
    "PostToolUse": [
      {
        "matcher": "*",
        "hooks": [
          {
            "type": "command",
            "command": "$CLAUDE_PROJECT_DIR/.claude/hooks/lib/hook-lib.sh",
            "timeout": 5
          }
        ]
      }
    ]
  }
}
JSON
    echo "‚úÖ Created settings.json"
fi

# Test the setup
echo -e "\nüè• Running diagnostic..."
"$HOOK_DIR/lib/hook-doctor.sh"

echo -e "\n‚úÖ Hook system ready!"
echo "Next steps:"
echo "  1. Review $HOOK_DIR/lib/hook-config.sh"
echo "  2. Customize your validation rules"
echo "  3. Create new hooks using the library"
echo "  4. Run 'HOOK_DEBUG=1 your-hook.sh' to debug"

