        },
        "hooks": {
          "$ref": "#/$defs/hookConfiguration"
        }
      },
      "required": ["description", "hooks"]
    },

    "hookConfiguration": {
      "type": "object",
      "properties": {
        "PreToolUse": {
          "type": "array",
          "items": {
            "$ref": "#/$defs/hookMatcher"
          }
        },
        "PostToolUse": {
          "type": "array", 
          "items": {
            "$ref": "#/$defs/hookMatcher"
          }
        }
      }
    },

    "hookMatcher": {
      "type": "object",
      "properties": {
        "matcher": {
          "type": "string",
          "description": "Pattern to match tool names (regex or glob)"
        },
        "hooks": {
          "type": "array",
          "items": {
            "$ref": "#/$defs/hookDefinition"
          }
        }
      },
      "required": ["matcher", "hooks"]
    },

    "hookDefinition": {
      "type": "object",
      "properties": {
        "type": {
          "type": "string",
          "enum": ["command", "script"],
          "description": "Type of hook execution"
        },
        "command": {
          "type": "string",
          "description": "Command or script path to execute"
        },
        "timeout": {
          "type": "integer",
          "minimum": 1,
          "maximum": 300,
          "description": "Timeout in seconds"
        },
        "continueOnFailure": {
          "type": "boolean",
          "description": "Whether to continue if hook fails"
        },
        "env": {
          "type": "object",
          "description": "Environment variables for this hook",
          "patternProperties": {
            "^[A-Z][A-Z0-9_]*$": {
              "type": "string"
            }
          }
        }
      },
      "required": ["type", "command"]
    }
  }
}

