{
  "$schema": "https://claude-code-hooks.schema.json",
  "version": "1.0.0",
  "description": "Hook orchestration manifest for clean settings.json",

  "env": {
    "HOOK_DIR": "$CLAUDE_PROJECT_DIR/.claude/hooks",
    "HOOK_CONFIG": "$CLAUDE_PROJECT_DIR/.claude/hooks/lib/hook-config.sh",
    "HOOK_DEBUG": "${HOOK_DEBUG:-0}",
    "HOOK_DRY_RUN": "${HOOK_DRY_RUN:-0}"
  },

  "presets": {
    "strict": {
      "description": "Full validation with blocking on errors",
      "hooks": {
        "PreToolUse": [
          {
            "matcher": "Write|Edit",
            "hooks": [
              {
                "type": "command",
                "command": "$HOOK_DIR/validate-code.sh",
                "timeout": 10,
                "continueOnFailure": false,
                "env": {
                  "ENABLE_SECRET_CHECK": "1",
                  "ENABLE_COMPLEXITY_CHECK": "1",
                  "ENABLE_TODO_TRACKING": "1"
                }
              }
            ]
          }
        ]
      }
    },

    "relaxed": {
      "description": "Validation with warnings only",
      "hooks": {
        "PreToolUse": [
          {
            "matcher": "Write|Edit",
            "hooks": [
              {
                "type": "command", 
                "command": "$HOOK_DIR/validate-code.sh",
                "timeout": 5,
                "continueOnFailure": true,
                "env": {
                  "ENABLE_SECRET_CHECK": "1",
                  "ENABLE_COMPLEXITY_CHECK": "0",
                  "ENABLE_TODO_TRACKING": "1"
                }
              }
            ]
          }
        ]
      }
    },

    "dev": {
      "description": "Development mode with debugging",
      "hooks": {
        "PreToolUse": [
          {
            "matcher": "Write|Edit",
            "hooks": [
              {
                "type": "command",
                "command": "$HOOK_DIR/validate-code.sh", 
                "timeout": 15,
                "continueOnFailure": true,
                "env": {
                  "HOOK_DEBUG": "1",
                  "HOOK_DRY_RUN": "0",
                  "ENABLE_SECRET_CHECK": "1",
                  "ENABLE_TODO_TRACKING": "1"
                }
              }
            ]
          }
        ]
      }
    },

    "disabled": {
      "description": "No hooks active", 
      "hooks": {}
    }
  },

  "active": "strict",

  "hooks": {
    "PreToolUse": "{{presets[active].hooks.PreToolUse}}",
    "PostToolUse": "{{presets[active].hooks.PostToolUse}}"
  }
}

{
  "$schema": "https://claude-code-project.schema.json",
  "version": "1.0.0",
  "description": "Project-specific configuration for Claude Code",

  "project": {
    "name": "ai claude",
    "type": "claude config lib",
    "description": "AI configuration and hook management system",
    "mainBranch": "main",
    "language": "shell",
    "framework": "claude-hooks"
  },

  "ignore": {
    "paths": [
      "node_modules",
      ".git", 
      "dist",
      "build",
      ".cache",
      "logs"
    ],
    "patterns": [
      "*.log",
      "*.tmp", 
      ".DS_Store",
      "*.backup.*",
      ".env.local"
    ]
  },

  "fileTypes": {
    "code": ["sh", "bash", "py", "js", "ts", "jsx", "tsx"],
    "config": ["json", "yaml", "yml", "toml"],
    "docs": ["md", "txt", "rst"],
    "ignore": ["log", "tmp", "cache", "lock"]
  },

  "validation": {
    "maxFileSize": "1MB",
    "allowedExtensions": ["sh", "json", "md", "py", "js", "ts"],
    "requireHeader": ["sh", "py"],
    "enforceLineEndings": "lf"
  },

  "tools": {
    "formatter": {
      "shell": "shfmt -i 2 -w",
      "json": "jq . -M",
      "markdown": "prettier --write --prose-wrap=preserve"
    },
    "linter": {
      "shell": "shellcheck",
      "json": "jq . -e",
      "markdown": "markdownlint"
    }
  }
}

{
  "$schema": "https://claude-code-debug.schema.json",
  "version": "1.0.0",
  "description": "Debug and environment configuration for easy troubleshooting",

  "env": {
    "system": {
      "CLAUDE_PROJECT_DIR": "$PWD",
      "HOOK_DIR": "$CLAUDE_PROJECT_DIR/.claude/hooks",
      "CONFIG_DIR": "$CLAUDE_PROJECT_DIR/.claude/config",
      "LOG_DIR": "$HOOK_DIR/logs"
    },

    "debug": {
      "HOOK_DEBUG": "${HOOK_DEBUG:-0}",
      "HOOK_DRY_RUN": "${HOOK_DRY_RUN:-0}",
      "HOOK_LOG_LEVEL": "${HOOK_LOG_LEVEL:-3}",
      "HOOK_VERBOSE": "${HOOK_VERBOSE:-0}"
    },

    "validation": {
      "ENABLE_SECRET_CHECK": "${ENABLE_SECRET_CHECK:-1}",
      "ENABLE_COMPLEXITY_CHECK": "${ENABLE_COMPLEXITY_CHECK:-1}",
      "ENABLE_AUTO_FORMAT": "${ENABLE_AUTO_FORMAT:-0}",
      "ENABLE_TODO_TRACKING": "${ENABLE_TODO_TRACKING:-1}",
      "ENABLE_TYPE_CHECK": "${ENABLE_TYPE_CHECK:-1}",
      "ENABLE_LINT_CHECK": "${ENABLE_LINT_CHECK:-1}"
    },

    "thresholds": {
      "MAX_COMPLEXITY": "${MAX_COMPLEXITY:-15}",
      "MAX_LINE_LENGTH": "${MAX_LINE_LENGTH:-120}",
      "MAX_TODO_COUNT": "${MAX_TODO_COUNT:-50}",
      "MAX_FILE_SIZE": "${MAX_FILE_SIZE:-1048576}"
    }
  },

  "debugModes": {
    "off": {
      "description": "No debugging",
      "env": {
        "HOOK_DEBUG": "0",
        "HOOK_VERBOSE": "0",
        "HOOK_LOG_LEVEL": "2"
      }
    },

    "basic": {
      "description": "Basic logging and info",
      "env": {
        "HOOK_DEBUG": "0", 
        "HOOK_VERBOSE": "1",
        "HOOK_LOG_LEVEL": "3"
      }
    },

    "full": {
      "description": "Full debugging with variable dumps",
      "env": {
        "HOOK_DEBUG": "1",
        "HOOK_VERBOSE": "1", 
        "HOOK_LOG_LEVEL": "4",
        "HOOK_DRY_RUN": "0"
      }
    },

    "dry-run": {
      "description": "Dry run mode - show what would happen",
      "env": {
        "HOOK_DEBUG": "1",
        "HOOK_VERBOSE": "1",
        "HOOK_DRY_RUN": "1",
        "HOOK_LOG_LEVEL": "4"
      }
    }
  },

  "activeMode": "basic",

  "shortcuts": {
    "enable-debug": "export HOOK_DEBUG=1 HOOK_VERBOSE=1",
    "disable-debug": "export HOOK_DEBUG=0 HOOK_VERBOSE=0", 
    "dry-run": "export HOOK_DRY_RUN=1 HOOK_DEBUG=1",
    "reset-env": "unset HOOK_DEBUG HOOK_VERBOSE HOOK_DRY_RUN HOOK_LOG_LEVEL",
    "show-env": "env | grep -E '^(HOOK_|CLAUDE_|ENABLE_|MAX_)' | sort"
  },

  "diagnostics": {
    "commands": {
      "test-config": "$HOOK_DIR/lib/hook-config.sh test",
      "show-config": "$HOOK_DIR/lib/hook-config.sh show", 
      "validate-manifest": "jq . $HOOK_DIR/manifest.json",
      "check-permissions": "ls -la $HOOK_DIR/*.sh",
      "tail-logs": "tail -f $LOG_DIR/hook.log"
    },

    "healthCheck": [
      "Check if jq is installed: command -v jq",
      "Check hook directory: test -d $HOOK_DIR", 
      "Check hook-lib exists: test -f $HOOK_DIR/lib/hook-lib.sh",
      "Check logs directory: test -d $LOG_DIR",
      "Validate manifest JSON: jq . $HOOK_DIR/manifest.json > /dev/null"
    ]
  }
}

